import { useCallback, useMemo } from 'react';
import useClient from './useClient.js';
export const useCheckPdpApi = () => {
    const { authenticatedPdpClient } = useClient();
    const getAllowedCheck = useCallback(async (body, pdp_url) => {
        return await authenticatedPdpClient(pdp_url).POST('/allowed', undefined, body);
    }, [authenticatedPdpClient]);
    const getAllowedUrlCheck = useCallback(async (requestInput, pdp_url) => {
        const apiBody = { ...requestInput };
        return await authenticatedPdpClient(pdp_url).POST('/allowed_url', undefined, apiBody);
    }, [authenticatedPdpClient]);
    return useMemo(() => ({
        getAllowedCheck,
        getAllowedUrlCheck,
    }), [getAllowedCheck, getAllowedUrlCheck]);
};
