import { useCallback, useMemo } from 'react';
import useClient from './useClient.js';
export const useMemberApi = () => {
    const { authenticatedApiClient } = useClient();
    const inviteNewMember = useCallback(async (body, inviter_name, inviter_email) => {
        return await authenticatedApiClient().POST(`/v2/members`, undefined, body, {
            inviter_email,
            inviter_name,
        });
    }, [authenticatedApiClient]);
    return useMemo(() => ({
        inviteNewMember,
    }), [inviteNewMember]);
};
