import { useCallback, useMemo } from 'react';
import useClient from './useClient.js';
export const useTenantApi = () => {
    const { authenticatedApiClient } = useClient();
    const createTenant = useCallback(async (body) => {
        return await authenticatedApiClient().POST('/v2/facts/{proj_id}/{env_id}/tenants', undefined, body);
    }, [authenticatedApiClient]);
    return useMemo(() => ({
        createTenant,
    }), [createTenant]);
};
